CREATE TABLE "CENTRAL_INTEREST" (
	"ci_idx"	int	GENERATED BY DEFAULT AS IDENTITY	NOT NULL,
	"STD_DAY"	varchar2(50 char)		NULL,
	"CTR_BANK"	varchar2(50 char)		NULL,
	"CTR_INTER"	float		NULL,
	"CTR_IS_RISE"	int		NULL,
	"CTR_LATEST_POINT"	number		NOT NULL,
	"CTR_CNG_DATE"	varchar2(50 char)		NULL,
	"CTR_NEXT_CONF"	varchar2(50 char)		NULL,
	"COUNTRY_NAME"	varchar2(50 char)		NOT NULL
);

COMMENT ON COLUMN "CENTRAL_INTEREST"."STD_DAY" IS '기준일자';

COMMENT ON COLUMN "CENTRAL_INTEREST"."CTR_BANK" IS '중앙은행';

COMMENT ON COLUMN "CENTRAL_INTEREST"."CTR_INTER" IS '금리';

COMMENT ON COLUMN "CENTRAL_INTEREST"."CTR_IS_RISE" IS '금리상승여부';

COMMENT ON COLUMN "CENTRAL_INTEREST"."CTR_LATEST_POINT" IS '금리 증감 percent-point';

COMMENT ON COLUMN "CENTRAL_INTEREST"."CTR_CNG_DATE" IS '최근 금리 결정 날짜';

COMMENT ON COLUMN "CENTRAL_INTEREST"."CTR_NEXT_CONF" IS '금리 결정회의 다음날짜';

COMMENT ON COLUMN "CENTRAL_INTEREST"."COUNTRY_NAME" IS '국가이름';

CREATE TABLE "EXCHANGE_RATE" (
	"er_idx"	int	GENERATED BY DEFAULT AS IDENTITY	NOT NULL,
	"COUNTRY_NAME"	varchar2(50 char)		NOT NULL,
	"STD_DAY"	varchar2(50 char)		NULL,
	"EXR_MONT_UNIT"	varchar2(50 char)		NULL,
	"EXR_STAD_RATE"	float		NULL
);

COMMENT ON COLUMN "EXCHANGE_RATE"."COUNTRY_NAME" IS '국가이름';

COMMENT ON COLUMN "EXCHANGE_RATE"."STD_DAY" IS '기준일자';

COMMENT ON COLUMN "EXCHANGE_RATE"."EXR_MONT_UNIT" IS '화폐이름';

COMMENT ON COLUMN "EXCHANGE_RATE"."EXR_STAD_RATE" IS '해당 1 화폐당 원화가격';

CREATE TABLE "FUTURES_MARKET" (
	"PRODUCT_NAME"	varchar2(50 char)		NULL,
	"UNIT"	varchar2(50 char)		NULL,
	"STD_DAY"	varchar2(50 char)		NULL,
	"FLUCTUATION_RATE"	float		NULL,
	"IS_RISE"	int		NULL,
	"PRICE"	float		NULL,
	"PRODUCT_LINE"	varchar2(50 char)		NULL
);

COMMENT ON COLUMN "FUTURES_MARKET"."PRODUCT_NAME" IS '상품이름';

COMMENT ON COLUMN "FUTURES_MARKET"."UNIT" IS '단위';

COMMENT ON COLUMN "FUTURES_MARKET"."STD_DAY" IS '기준일자';

COMMENT ON COLUMN "FUTURES_MARKET"."FLUCTUATION_RATE" IS '변동율(%)';

COMMENT ON COLUMN "FUTURES_MARKET"."IS_RISE" IS '전일대비 상승여부';

COMMENT ON COLUMN "FUTURES_MARKET"."PRICE" IS '가격';

COMMENT ON COLUMN "FUTURES_MARKET"."PRODUCT_LINE" IS '상품군';

CREATE TABLE "AGRICULTURE" (
	"ag_idx"	int	GENERATED BY DEFAULT AS IDENTITY	NOT NULL,
	"PRODUCT_NAME"	varchar2(50 char)		NULL,
	"STD_DAY"	varchar2(50 char)		NULL,
	"CURRENT_MONTH_CONTRACT"	varchar2(50 char)		NULL,
	"FUTURES_EXCHANGE_NAME"	varchar2(50 char)		NULL
);

COMMENT ON COLUMN "AGRICULTURE"."PRODUCT_NAME" IS '상품이름';

COMMENT ON COLUMN "AGRICULTURE"."STD_DAY" IS '기준일자';

COMMENT ON COLUMN "AGRICULTURE"."CURRENT_MONTH_CONTRACT" IS '월물';

COMMENT ON COLUMN "AGRICULTURE"."FUTURES_EXCHANGE_NAME" IS '거래소명';

CREATE TABLE "ENERGY" (
	"en_idx"	int	GENERATED BY DEFAULT AS IDENTITY	NOT NULL,
	"PRODUCT_NAME"	varchar2(50 char)		NULL,
	"STD_DAY"	varchar2(50 char)		NULL,
	"CURRENT_MONTH_CONTRACT"	varchar2(50 char)		NULL,
	"FUTURES_EXCHANGE_NAME"	varchar2(50 char)		NULL
);

COMMENT ON COLUMN "ENERGY"."PRODUCT_NAME" IS '상품이름';

COMMENT ON COLUMN "ENERGY"."STD_DAY" IS '기준일자';

COMMENT ON COLUMN "ENERGY"."CURRENT_MONTH_CONTRACT" IS '월물';

COMMENT ON COLUMN "ENERGY"."FUTURES_EXCHANGE_NAME" IS '거래소명';

CREATE TABLE "STOCK_INDEX" (
	"si_idx"	int	GENERATED BY DEFAULT AS IDENTITY   NOT NULL,
	"COUNTRY_NAME"	varchar2(50 char)		NULL,
	"STD_DAY"	varchar2(50 char)		NULL,
	"SI_NAME"	varchar2(50 char)		NULL,
	"SI_PRICE"	float		NULL,
	"SI_FL_RATE"	float		NULL,
	"SI_IS_RISE"	int		NULL
);

COMMENT ON COLUMN "STOCK_INDEX"."COUNTRY_NAME" IS '국가이름';

COMMENT ON COLUMN "STOCK_INDEX"."STD_DAY" IS '기준일자';

COMMENT ON COLUMN "STOCK_INDEX"."SI_NAME" IS '주가지수이름';

COMMENT ON COLUMN "STOCK_INDEX"."SI_PRICE" IS '주가지수종가';

COMMENT ON COLUMN "STOCK_INDEX"."SI_FL_RATE" IS '지수등락률(%)';

COMMENT ON COLUMN "STOCK_INDEX"."SI_IS_RISE" IS '지수상승여부';

ALTER TABLE "STOCK_INDEX" ADD CONSTRAINT "PK_STOCK_INDEX" PRIMARY KEY (
	"si_idx"
);

CREATE TABLE "SOVEREIGN_YIELD" (
	"sy_idx"	int	GENERATED BY DEFAULT AS IDENTITY	NOT NULL,
	"STD_DAY"	varchar2(50 char)		NULL,
	"SY_Y_CNT"	int		NULL,
	"SY_BOND_YIELD"	float		NULL,
	"SY_IS_RISE"	int		NULL,
	"COUNTRY_NAME"	varchar2(50 char)		NOT NULL
);

COMMENT ON COLUMN "SOVEREIGN_YIELD"."STD_DAY" IS '기준일자';

COMMENT ON COLUMN "SOVEREIGN_YIELD"."SY_Y_CNT" IS '채권만기년도(년)';

COMMENT ON COLUMN "SOVEREIGN_YIELD"."SY_BOND_YIELD" IS '채권수익률(%)';

COMMENT ON COLUMN "SOVEREIGN_YIELD"."SY_IS_RISE" IS '채권상승여부';

COMMENT ON COLUMN "SOVEREIGN_YIELD"."COUNTRY_NAME" IS '국가이름';

CREATE TABLE "COUNTRY" (
	"COUNTRY_NAME"	varchar2(50 char)		NOT NULL,
	"MARKET_CAP"	float		NULL,
	"COUNTRY_NAME_EN"	varchar2(50 char)		NULL
);

COMMENT ON COLUMN "COUNTRY"."COUNTRY_NAME" IS '국가이름';

COMMENT ON COLUMN "COUNTRY"."MARKET_CAP" IS '시가총액(100만 달러)';

COMMENT ON COLUMN "COUNTRY"."COUNTRY_NAME_EN" IS '국가영어이름';

COMMENT ON COLUMN "COUNTRY"."STD_DAY" IS '기준일자';

ALTER TABLE "CENTRAL_INTEREST" ADD CONSTRAINT "PK_CENTRAL_INTEREST" PRIMARY KEY (
	"ci_idx"
);

ALTER TABLE "EXCHANGE_RATE" ADD CONSTRAINT "PK_EXCHANGE_RATE" PRIMARY KEY (
	"er_idx"
);

ALTER TABLE "FUTURES_MARKET" ADD CONSTRAINT "PK_FUTURES_MARKET" PRIMARY KEY (
	"PRODUCT_NAME"
);

ALTER TABLE "AGRICULTURE" ADD CONSTRAINT "PK_AGRICULTURE" PRIMARY KEY (
	"ag_idx"
);

ALTER TABLE "ENERGY" ADD CONSTRAINT "PK_ENERGY" PRIMARY KEY (
	"en_idx"
);

ALTER TABLE "STOCK_INDEX" ADD CONSTRAINT "PK_STOCK_INDEX" PRIMARY KEY (
	"si_idx"
);

ALTER TABLE "SOVEREIGN_YIELD" ADD CONSTRAINT "PK_SOVEREIGN_YIELD" PRIMARY KEY (
	"sy_idx"
);

ALTER TABLE "COUNTRY" ADD CONSTRAINT "PK_COUNTRY" PRIMARY KEY (
	"COUNTRY_NAME"
);

